// OBS! "str" måste vara av längden 3 eller mer.
void int_to_str(char* str, int* i)
{
	int s0 = (*i / 100);
	int s1 = (*i - 100 * s0) / 10;
	int s2 = (*i - 100 * s0) % 10;
	
	str[0] = s0 + '0';
	str[1] = s1 + '0';
	str[2] = s2 + '0';
}

